export default async function({ page, context }) {
  const usuario = context.usuario;
  const senha = context.senha;
  const numero = context.numero_processo;
  try {
    await page.setViewport({ width: 1920, height: 1080 });
    await page.setUserAgent(
      'Mozilla/5.0 (Windows NT 10.0; Win64; x64) ' +
      'AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36'
    );

    await page.goto('https://pje.cloud.tjpe.jus.br/1g/login.seam', {
      waitUntil: 'networkidle2',
      timeout: 60000
    });
    await page.waitForSelector('#username', { visible: true, timeout: 30000 });
    await page.waitForSelector('#password', { visible: true, timeout: 30000 });
    await page.type('#username', usuario, { delay: 50 });
    await page.type('#password', senha, { delay: 50 });
    await page.click('#kc-login');
    await page.waitForNavigation({ waitUntil: 'networkidle2', timeout: 60000 });

    await page.waitForTimeout(2000);
    await page.goto('https://portaldeservicos.pdpj.jus.br/consulta', {
      waitUntil: 'networkidle2',
      timeout: 60000
    });
    await page.waitForTimeout(2000);

    const jwt = await page.evaluate(() => localStorage.getItem('access_token'));
    if (!jwt) {
      return {
        success: false,
        error: 'access_token nÃ£o encontrado em localStorage.'
      };
    }

    const consultaUrl = `https://portaldeservicos.pdpj.jus.br/api/v2/processos/${numero}`;
    const resposta = await page.evaluate(
      async ({ url, token }) => {
        const resp = await fetch(url, {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
            'Accept': 'application/json'
          }
        });
        if (!resp.ok) {
          const txt = await resp.text();
          throw new Error(`Erro na API PDPJ: ${resp.status} - ${txt}`);
        }
        return resp.json();
      },
      { url: consultaUrl, token: jwt }
    );

    return {
      success: true,
      dados_processo: resposta,
      type: 'application/json'
    };
  } catch (err) {
    return {
      success: false,
      error: err.message
    };
  }
}
